import React from 'react';

function ImagePopup(props){
  const [popupClass, setPopupClass] = React.useState('');
  React.useEffect(() => {
    setPopupClass(`popup element-popup popup_opened`);
    return () => {
      setPopupClass(`popup element-popup`);
    }
  })
  
    return (
        
        <div className={popupClass}>
          <div className="element-popup__container">
            <button type="button" className="popup__close-icon" onClick={props.onClose}/>
            <img
              src={props.imageSrc}
              className="element-popup__image"
              alt={props.name}
            />
            <p className="element-popup__text">{props.name}</p>
          </div>
        </div>
    )
}

export default ImagePopup;
