import React from "react";
import { api } from "../utils/Api";
import { currentUserContext } from "../contexts/CurrentUserContext";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false); /* profile-edit-popup */
  const [isNewAvatarPopupOpen, setIsNewAvatarPopupOpen] = React.useState(false); /* new-avatar-popup */
  const [isCardDeletePopupOpen, setIsDeletePopupOpen] = React.useState(false); /* card-delete-popup */
  const [isCardAddPopupOpen, setIsCardAddPopupOpen] = React.useState(false); /* card-add-popup */

  /*_states_of_image|imagePopup______*/
  const [isElementPopupOpen, setIsElementPopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({ name: "", link: "" });
  /*---------------------------*/

  /* currentUser_state */
  const [currentUser, setCurrentUser] = React.useState({
    avatar: "",
    name: "",
    description: "",
    id: ""
  });
  /*---------------------------*/

  /* user_avatar-input_ref */
  const avatarInputRef = React.useRef();
  console.log(avatarInputRef);
  /*---------------------------*/

  /*_state_of_cardArray______*/
  const [cards, setCards] = React.useState([]);
  /*---------------------------*/

  React.useEffect(() => {
    api
      .getInfoAboutUser()
      .then((res) => {
        setCurrentUser({
          ...currentUser,
          avatar: res.avatar,
          name: res.name,
          description: res.about,
          id: res._id,
        });
      })
      .catch((err) => {
        console.log(err);
      });

      api
      .getInitialCards()
      .then((res) => {
        setCards(res.reverse());
      })
      .catch((err) => {
        console.log(err);
      });

  }, []);

  /* functions_for_user-data-updates */
  function handleUpdateUserInfo(newUserInfo) {
    console.log(newUserInfo);
    api
      .setInfoAboutUser(newUserInfo)
      .then((res) => {
        setCurrentUser({
          ...currentUser,
          name: res.name,
          description: res.about,
        });
        setIsEditProfilePopupOpen(false);
      })
      .catch((err) => {
        console.log(err);
      });
  }

  function handleUpdateUserAvatar(){
    console.log(avatarInputRef.current.value);
    api
      .uploadNewAvatar(avatarInputRef.current.value)
      .then((res) => {
        setCurrentUser({
          ...currentUser,
          avatar: res.avatar,
        });
        setIsNewAvatarPopupOpen(false);
      })
      .catch((err) => {
        console.log(err);
      });
  }
  /*---------------------------*/

  /* functions_for_cards */
  function handleCardLike(card) {
    console.log(card);
    const isLiked = card.likes.some(i => i._id === currentUser.id);
    api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
  } 

  function handleCardDelete(card){
    api.deleteCard(card._id)
    .then((res) => {
      console.log(res);
      setCards((state) => state.filter((c) => c._id !== card._id));
    })
    .catch((err) => {
      console.log(err);
    });
  }
  /*---------------------------*/
  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsNewAvatarPopupOpen(false);
    setIsDeletePopupOpen(false);
    setIsCardAddPopupOpen(false);
    setIsElementPopupOpen(false);

    setSelectedCard({ name: "", link: "", });
  }

  return (
    <div className="App page">
      <currentUserContext.Provider value={currentUser}>
        <Header />
        <Main
          onEditProfile={() => {
            setIsEditProfilePopupOpen(true);
          }}
          onCardAdd={() => {
            setIsCardAddPopupOpen(true);
          }}
          onNewAvatar={() => {
            setIsNewAvatarPopupOpen(true);
          }}
          onCardClick={(name, link) => {
            setSelectedCard({ name: name, link: link, });
            setIsElementPopupOpen(true);
          }}
          onCardLike={(card) => {
            handleCardLike(card);
          }}
          onCardDelete={(card) => {
            handleCardDelete(card);
          }}
        />
        <Footer />

        {/*___________overlays*_____________*/}

        {/*.new-avatar-popup.*/}
        <EditAvatarPopup 
          isOpen={isNewAvatarPopupOpen}
          onClose={closeAllPopups}
          onUpdateUserAvatar={handleUpdateUserAvatar}
          ref={avatarInputRef}
        />

        {/*.profile-edit-popup.*/}
        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUserInfo={(newInfo) => {
            handleUpdateUserInfo(newInfo);
          }}
        />

        {/*.card-delete-popup.*/}
        <PopupWithForm
          name="card-delete"
          title="Вы уверены?"
          formName="delete"
          isOpen={isCardDeletePopupOpen}
          formClass="popup__form_type_delete-popup"
          buttonLabel="Да"
          onClose={closeAllPopups}
        ></PopupWithForm>

        {/*.card-add-popup.*/}
        <PopupWithForm
          name="card-add"
          title="Новое место"
          formName="add"
          isOpen={isCardAddPopupOpen}
          formClass="popup__form_type_card-add"
          buttonLabel="Создать"
          onClose={closeAllPopups}
        >
          <input
            type="text"
            className="popup__input popup__input_type_card-name"
            name="cardname"
            id="cardname-input"
            minLength={2}
            maxLength={30}
            required
            placeholder="Название"
          />
          <span className="popup__form-placeholder cardname-input-error" />
          <input
            type="url"
            className="popup__input popup__input_type_card-link"
            name="link"
            id="link-input"
            required
            placeholder="Ссылка на картинку"
          />
          <span className="popup__form-placeholder link-input-error" />
        </PopupWithForm>

        {/*.element-popup.*/}
        <ImagePopup
          onClose={closeAllPopups}
          isOpen={isElementPopupOpen}
          card={selectedCard}
        />
      </currentUserContext.Provider>
      {/* весь код отформатирован при помощи prettier(vs-code) */}
    </div>
  );
}

export default App;
