import React from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);  {/* profile-edit-popup */}
  const [isNewAvatarPopupOpen, setIsNewAvatarPopupOpen] = React.useState(false);  {/* new-avatar-popup */}
  const [isCardDeletePopupOpen, setIsDeletePopupOpen] = React.useState(false);  {/* card-delete-popup */}
  const [isCardAddPopupOpen, setIsCardAddPopupOpen] = React.useState(false);  {/* card-add-popup */}

  {/*_states_of_image|imagePopup______*/}
  const [isElementPopupOpen, setIsElementPopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({'name':'', 'link':''});
  /*---------------------------*/


  function closeAllPopups(){
    setIsEditProfilePopupOpen(false);
    setIsNewAvatarPopupOpen(false);
    setIsDeletePopupOpen(false);
    setIsCardAddPopupOpen(false);
    setIsElementPopupOpen(false);
    setSelectedCard({name: '', link: ''})
  }

  return (
    <div className="App page">
      <Header />
      <Main
        onEditProfile={() => {
          setIsEditProfilePopupOpen(true);
        }}
        onCardAdd={() => {
          setIsCardAddPopupOpen(true);
        }}
        onNewAvatar={() => {
          setIsNewAvatarPopupOpen(true);
        }}
        onCardDelete={() => {
          setIsDeletePopupOpen(true);
        }}
        onCardClick={(name, link) => {
          setSelectedCard({name: name, link: link});
          setIsElementPopupOpen(true);
        }}
      />
      <Footer />

      {/*___________overlays*_____________*/}

      {/*.new-avatar-popup.*/}
        <PopupWithForm
          name="new-avatar"
          title="Обновить аватар"
          formName="edit"
          isOpen={isNewAvatarPopupOpen}
          formClass="popup__form_type_new-avatar"
          buttonLabel="Сохранить"
          onClose={closeAllPopups}
        >
          <input
            type="url"
            className="popup__input popup__input_type_avatarlink"
            name="avatarlink"
            id="avatarlink-input"
            required
            placeholder="Ссылка"
          />
          <span className="popup__form-placeholder avatarlink-input-error" />
        </PopupWithForm>

      {/*.profile-edit-popup.*/}
        <PopupWithForm
          name="profile-edit"
          title="Редактировать профиль"
          formName="change"
          isOpen={isEditProfilePopupOpen}
          formClass="popup__form_type_edit"
          buttonLabel="Сохранить"
          onClose={closeAllPopups}
        >
          <input
            type="text"
            className="popup__input popup__input_type_name"
            name="fullname"
            required
            id="fullname-input"
            minLength={2}
            maxLength={40}
            placeholder="Имя"
          />
          <span className="popup__form-placeholder fullname-input-error" />
          <input
            type="text"
            className="popup__input popup__input_type_about"
            name="about"
            id="about-input"
            required
            minLength={2}
            maxLength={200}
            placeholder="о себе"
          />
          <span className="popup__form-placeholder about-input-error" />
        </PopupWithForm>

      {/*.card-delete-popup.*/}
        <PopupWithForm
          name="card-delete"
          title="Вы уверены?"
          formName="delete"
          isOpen={isCardDeletePopupOpen}
          formClass="popup__form_type_delete-popup"
          buttonLabel="Да"
          onClose={closeAllPopups}
        ></PopupWithForm>

      {/*.card-add-popup.*/}
        <PopupWithForm
          name="card-add"
          title="Новое место"
          formName="add"
          isOpen={isCardAddPopupOpen}
          formClass="popup__form_type_card-add"
          buttonLabel="Создать"
          onClose={closeAllPopups}
        >
          <input
            type="text"
            className="popup__input popup__input_type_card-name"
            name="cardname"
            id="cardname-input"
            minLength={2}
            maxLength={30}
            required
            placeholder="Название"
          />
          <span className="popup__form-placeholder cardname-input-error" />
          <input
            type="url"
            className="popup__input popup__input_type_card-link"
            name="link"
            id="link-input"
            required
            placeholder="Ссылка на картинку"
          />
          <span className="popup__form-placeholder link-input-error" />
        </PopupWithForm>

      {/*.element-popup.*/}
          <ImagePopup
            onClose={closeAllPopups}
            isOpen={isElementPopupOpen}
            card={selectedCard}
          />

      {/* весь код отформатирован при помощи prettier(vs-code) */}

      {/**
            setSelectedCard({
              ...selectedCard,
              name: `${card.name}`,
              link: `${card.link}`,
            });

            name={selectedCard.name}
            imageSrc={selectedCard.link}
            isOpen={isElementPopupOpen}
       */}
    </div>
  );
}

export default App;
