import React from "react";
import deleteIconPath from "../images/element__delete-icon.svg";
import { currentUserContext } from "../contexts/CurrentUserContext";

function Card(obj) {
  const newCard = obj.card;
  const functionOnClose = obj.onClose;
  const functionOnClick = obj.onClick;
  const currentUser = React.useContext(currentUserContext);
  const isOwner = (newCard._id === currentUser._id);
  const isLiked = newCard.likes.some(i => i._id === currentUser._id);
  const cardLikeButtonClass = isLiked ? 'element__like_type_active' : 'element__like';
// Создаём переменную, которую после зададим в `className` для кнопки удаления
  return (
    <article className="element">
      <img
        src={newCard.link}
        className="element__image"
        onClick={functionOnClick}
        alt={newCard.name.toLowerCase()}
      />

      { isOwner && 
        <img
          src={deleteIconPath}
          className="element__delete-icon"
          onClick={functionOnClose}
          alt="иконка удаления"
        /> 
      }

      <div className="elements__ordinary-container">
        <h2 className="element__text">{newCard.name}</h2>
        <div className="element__like-container">
          <button type="button" className='' />
          <span className="element__counter">{newCard.likes.length}</span>
        </div>
      </div>
    </article>
  );
}

export default Card;
