import React from "react";
import PopupWithForm from "./PopupWithForm";

const EditAvatarPopup = React.forwardRef((props, ref) => {

  let submitButtonText = props.isLoading ? "Изменение..." : "Сохранить";

  function handleSubmit(e) {
    e.preventDefault();
    props.onUpdateUserAvatar();
    e.target.reset();
  }

  React.useEffect(() => {
    console.log(ref.current)
    ref.current.value.reset();
  }, [props.isOpen]);

  return (
    <PopupWithForm
      name="new-avatar"
      title="Обновить аватар"
      formName="edit"
      isOpen={props.isOpen}
      formClass="popup__form_type_new-avatar"
      buttonLabel={submitButtonText}
      onClose={props.onClose}
      onSubmit={handleSubmit}
    >
      <input
        type="url"
        className="popup__input popup__input_type_avatarlink"
        name="avatarlink"
        id="avatarlink-input"
        placeholder="Введите URL"
        required
        ref={ref}
      />
      <span className="popup__form-placeholder avatarlink-input-error" />
    </PopupWithForm>
  );
});
export default EditAvatarPopup;
