import React from "react";

function Login() {
  const [formEmail, setFormEmail] = React.useState("");
  const [formPassword, setFormPassword] = React.useState("");
  const [authInfo, setAuthInfo] = React.useState({ email: '', password: ''});

  function handleChange(e){
    setAuthInfo({
      [e.target.name]: e.target.value,
    })
  }

  function handleSubmit(e){
    e.preventDefault();
    console.log(authInfo);
  }
  return (
    <div className="auth"> {/* сделал один класс, ибо они одинаковые. в будущем можно будет делать _type_login если нужно будет добавлять уникальности */}
      <h2 className="auth__title">Вход</h2>
      <form className="auth__form" onSubmit={handleSubmit}>
        <fieldset className="auth__form-set">
          <input
            type="email"
            name="auth-log-email"
            placeholder="Email"
            className="auth__input"
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="auth-reg-password"
            placeholder="Пароль"
            className="auth__input"
            onChange={handleChange}
            required
          />
          <button type="submit" className="auth__button auth__button_type_log">
            Войти
          </button>
        </fieldset>
      </form>
    </div>
  );
}

export default Login;
