import React from "react";

const initialFormData = Object.freeze({
  firstInput: "",
  secondInput: ""
});

function PopupWithForm(props) {
  const [formData, updateFormData] = React.useState(initialFormData);

  function handleChange(e){
    updateFormData({
      ...formData,
      [e.target.name]: e.target.value.trim()
    });
  };

  function handleSubmit(e){
    e.preventDefault()
    console.log(formData);
    // ... submit to API or something
  };

  let popupClass = props.isOpen ? `popup ${props.name}-popup popup_opened` : `popup ${props.name}-popup`;
  console.log(popupClass);
  return (
    <article className={popupClass}>
      <div className={`popup__container ${props.name}-popup__container`}>
        <button
          type="button"
          className="popup__close-icon"
          onClick={props.onClose}
        />

        <h2 className="popup__title">{props.title}</h2>

        <form
          className={`popup__form ${props.formClass}`}
          onSubmit={(evt) => {
            evt.preventDefault();
            
          }}
          name={props.formName}
          noValidate
        >
          <fieldset className="popup__form-set">
            {props.children}
            <button type="submit" className="popup__button">
              {props.buttonLabel}
            </button>
          </fieldset>
        </form>
      </div>
    </article>
  );
}

export default PopupWithForm;
