import React from "react";
import { api } from "../utils/Api";
import Card from "./Card";
import profileImageAfterPath from "../images/profile__image_after.svg";
import defaultProfileImagePath from "../images/profile__image.jpg"; {/* дефолтная картинка, если у пользователя отсутствует соединение */}

function Main(props) 
{{/*_states_of_userData______*/}
  const [userAvatar, setUserAvatar] = React.useState(`${defaultProfileImagePath}`);
  const [userDescription, setUserDescription] = React.useState("");
  const [userName, setUserName] = React.useState("");
  /*---------------------------*/

  {/*_state_of_cardArray______*/}
  const [cards, setCards] = React.useState([]);
  /*---------------------------*/

  React.useEffect(() => {
    api
      .getInfoAboutUser()
      .then((res) => {
        setUserName(res.name);
        setUserDescription(res.about);
        setUserAvatar(res.avatar);
      })
      .catch((err) => {
        console.log(err);
      });

    api
      .getInitialCards()
      .then((res) => {
        setCards(res);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__main-container">
          <div
            className="profile__picture-container"
            onClick={props.onNewAvatar}
          >
            <img
              src={userAvatar}
              className="profile__image"
              alt="аватарка пользователя"
            />
            <img
              src={profileImageAfterPath}
              className="profile__avatar-edit"
              alt="Редактирование"
            />
          </div>
          <div className="profile__info">
            <div className="profile__text">
              <h1 className="profile__name">{userName}</h1>
              <p className="profile__about">{userDescription}</p>
            </div>
            <button
              type="button"
              className="profile__edit-button"
              aria-label="редактировать профиль"
              onClick={props.onEditProfile}
            />
          </div>
        </div>
        <button
          type="button"
          className="profile__button"
          onClick={props.onCardAdd}
        />
      </section>
      <section className="elements">
        {cards.map((card, i) => (
          <Card
            card={card}
            key={card._id}
            onClose={props.onCardDelete}
            onCardClick={onImagePopup}
          />
        ))}
      </section>
    </main>
  );
}

export default Main;
