import React from "react";
import { api } from "../utils/Api";
import { currentUserContext } from "../contexts/CurrentUserContext";
import Card from "./Card";
import profileImageAfterPath from "../images/profile__image_after.svg";
import defaultProfileImagePath from "../images/profile__image.jpg"; {/* дефолтная картинка, если у пользователя отсутствует соединение */}

function Main(props) {
  const user = React.useContext(currentUserContext);
  console.log(user);
  /*_state_of_cardArray______*/
  const [cards, setCards] = React.useState([]);
  /*---------------------------*/

  React.useEffect(() => {
    api
      .getInitialCards()
      .then((res) => {
        setCards(res);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  function handleCardLike(card) {
    console.log(card);
    const isLiked = card.likes.some(i => {
      console.log(i._id, user.id, i._id === user.id);
      return i._id === user.id
    });
    
    api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
} 

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__main-container">
          <div
            className="profile__picture-container"
            onClick={props.onNewAvatar}
          >
            <img
              src={user.avatar}
              className="profile__image"
              alt="аватарка пользователя"
            />
            <img
              src={profileImageAfterPath}
              className="profile__avatar-edit"
              alt="Редактирование"
            />
          </div>
          <div className="profile__info">
            <div className="profile__text">
              <h1 className="profile__name">{user.name}</h1>
              <p className="profile__about">{user.description}</p>
            </div>
            <button
              type="button"
              className="profile__edit-button"
              aria-label="редактировать профиль"
              onClick={props.onEditProfile}
            />
          </div>
        </div>
        <button
          type="button"
          className="profile__button"
          onClick={props.onCardAdd}
        />
      </section>
      <section className="elements">
        {cards.map(card => (
          <Card
            card={card}
            key={card._id}
            currentUser={user}
            onClose={props.onCardDelete}
            onClick={() => {
              props.onCardClick(card.name, card.link)
            }}
            onLike={() => {
              handleCardLike(card);
            }}
          />
        ))} {/* первоначальный рендер карточек */}
      </section>
    </main>
  );
}

export default Main;
