import React from "react";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup"
import profileImagePath from "../images/profile__image.jpg";
import profileImageAfterPath from "../images/profile__image_after.svg";
import deleteIconPath from "../images/element__delete-icon.svg";
import cardImagePath from "../images/elements__card__main-image(1).png"; {/*test*/}



function Main() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);  {/* profile-edit-popup */}
  const [isNewAvatarPopupOpen, setIsNewAvatarPopupOpen] = React.useState(false);  {/* new-avatar-popup */}
  const [isCardDeletePopupOpen, setIsDeletePopupOpen] = React.useState(false);  {/* card-delete-popup */}
  const [isCardAddPopupOpen, setIsCardAddPopupOpen] = React.useState(false);  {/* card-add-popup */}
  const [isElementPopupOpen, setIsElementPopupOpen] = React.useState(false); {/* element-popup */}
  /*---------------------------*/

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__main-container">
          <div
            className="profile__picture-container"
            onClick={() => {
              setIsNewAvatarPopupOpen(true);
            }}
          >
            <img
              src={profileImagePath}
              className="profile__image"
              alt="аватарка пользователя"
            />
            <img
              src={profileImageAfterPath}
              className="profile__avatar-edit"
              alt="Редактирование"
            />
          </div>
          <div className="profile__info">
            <div className="profile__text">
              <h1 className="profile__name">Жак-Ив Кусто</h1>
              <p className="profile__about">Исследователь океана</p>
            </div>
            <button
              type="button"
              className="profile__edit-button"
              aria-label="редактировать профиль"
              onClick={() => {
                setIsEditProfilePopupOpen(true);
              }}
            />
          </div>
        </div>
        <button
          type="button"
          className="profile__button"
          onClick={() => {
            setIsCardAddPopupOpen(true);
          }}
        />
      </section>
      <section className="elements">
        <article className="element">
          <img src={cardImagePath} className="element__image" alt="карачаевск" onClick={() => {
            console.log('click');
            setIsElementPopupOpen(true);
          }}/>
          <img src={deleteIconPath} className="element__delete-icon" alt="иконка удаления" onClick={() => {
            setIsDeletePopupOpen(true);
          }}/>
          <div className="elements__ordinary-container">
            <h2 className="element__text">Карачаевск</h2>
            <div className="element__like-container">
              <button type="button" className="element__like" />
              <span className="element__counter">0</span>
            </div>
          </div>
        </article>
        {/*___________overlays*_____________*/}

        {/*--element-popup--*/}
        {isElementPopupOpen && (
          <ImagePopup name="Карачаево-Черкесия" onClose={() => {
            setIsElementPopupOpen(false);
          }}/>
        )}
      </section>

      {/*.new-avatar-popup.*/}
      {isNewAvatarPopupOpen && (
        <PopupWithForm
          name="new-avatar"
          title="Обновить аватар"
          formName="edit"
          formClass="popup__form_type_new-avatar"
          buttonLabel="Сохранить"
          isOpen={true}
          onClose={() => {
            setIsNewAvatarPopupOpen(false);
          }}
        >
          <input
            type="url"
            className="popup__input popup__input_type_avatarlink"
            name="avatarlink"
            id="avatarlink-input"
            required
            placeholder="Ссылка"
          />
          <span className="popup__form-placeholder avatarlink-input-error" />
        </PopupWithForm>
      )}

      {/*.profile-edit-popup.*/}
      {isEditProfilePopupOpen && (
        <PopupWithForm
          name="profile-edit"
          title="Редактировать профиль"
          formName="change"
          formClass="popup__form_type_edit"
          buttonLabel="Сохранить"
          isOpen={true}
          onClose={() => {
            setIsEditProfilePopupOpen(false);
          }}
        >
          <input
            type="text"
            className="popup__input popup__input_type_name"
            name="fullname"
            required
            id="fullname-input"
            minLength={2}
            maxLength={40}
            placeholder="Имя"
          />
          <span className="popup__form-placeholder fullname-input-error" />
          <input
            type="text"
            className="popup__input popup__input_type_about"
            name="about"
            id="about-input"
            required
            minLength={2}
            maxLength={200}
            placeholder="о себе"
          />
          <span className="popup__form-placeholder about-input-error" />
        </PopupWithForm>
      )}

      {/*.card-delete-popup.*/}
      {isCardDeletePopupOpen && (
        <PopupWithForm
          name="card-delete"
          title="Вы уверены?"
          formName="delete"
          formClass="popup__form_type_delete-popup"
          buttonLabel="Да"
          isOpen={true}
          onClose={() => {
            setIsDeletePopupOpen(false);
          }}
        >
        </PopupWithForm>
      )}

      {/*.card-add-popup.*/}
      {isCardAddPopupOpen && (
        <PopupWithForm
          name="card-add"
          title="Новое место"
          formName="add"
          formClass="popup__form_type_card-add"
          buttonLabel="Создать"
          isOpen={true}
          onClose={() => {
            setIsCardAddPopupOpen(false);
          }}
        >
          <input
            type="text"
            className="popup__input popup__input_type_card-name"
            name="cardname"
            id="cardname-input"
            minLength={2}
            maxLength={30}
            required
            placeholder="Название"
          />
          <span className="popup__form-placeholder cardname-input-error" />
          <input
            type="url"
            className="popup__input popup__input_type_card-link"
            name="link"
            id="link-input"
            required
            placeholder="Ссылка на картинку"
          />
          <span className="popup__form-placeholder link-input-error" />
        </PopupWithForm>
      )}
    </main>
  );
}

export default Main;
