import React from "react";

function PopupWithForm(props) {
  console.log(props);
  [isPopupOpen, setIsPopupOpen] = React.useState(props.isOpen);
  let popupClass = isPopupOpen ? `popup ${props.name}-popup popup_opened` : `popup ${props.name}-popup`;

  return (
    <article className={popupClass}>
      <div className="popup__container">
        <button
          type="button"
          className="popup__close-icon"
          onClick={props.onClose}
        />

        <h2 className="popup__title">{props.title}</h2>

        <form
          className={`popup__form ${props.formClass}`}
          name={props.formName}
          noValidate
        >
          <fieldset className="popup__form-set">
            {props.children}
            <button type="submit" className="popup__button" disabled>
              {props.buttonLabel}
            </button>
          </fieldset>
        </form>
      </div>
    </article>
  );
}

export default PopupWithForm;
