import React from "react";
import PopupWithForm from "./PopupWithForm";
import { api } from "../utils/Api";
import Card from "./Card";
import profileImageAfterPath from "../images/profile__image_after.svg";
import ImagePopup from "./ImagePopup"
import defaultProfileImagePath from "../images/profile__image.jpg"; {/* дефолтная картинка, если у пользователя отсутствует соединение */}


function Main(props) {
  {/*_states_of_overlays______*/}
  {/* profile-edit-popup */}
  const [isNewAvatarPopupOpen, setIsNewAvatarPopupOpen] = React.useState(false);  {/* new-avatar-popup */}
  const [isCardDeletePopupOpen, setIsDeletePopupOpen] = React.useState(false);  {/* card-delete-popup */}
  /*---------------------------*/

  {/*_states_of_userData______*/}
  const [userDescription, setUserDescription] = React.useState('Hard to write on React');
  const [userAvatar, setUserAvatar] = React.useState(`${defaultProfileImagePath}`);
  const [userName, setUserName] = React.useState('Typical-nedo-junior');
  /*---------------------------*/

  {/*_state_of_imagePopup______*/}
  const [isElementPopupOpen, setIsElementPopupOpen] = React.useState(false); {/* element-popup */}
  const [data, setData] = React.useState({'name':'', 'link':''});
  /*---------------------------*/

  {/*_state_of_cardArray______*/}
  const [cards, setCards] = React.useState([]);
  /*---------------------------*/

  React.useEffect(() => {
    api.getInfoAboutUser()
      .then((res) => {
        setUserName(res.name);
        setUserDescription(res.about);
        setUserAvatar(res.avatar)
      }).catch((err) => {
        console.log(err);
      });

      api.getInitialCards()
      .then((res) => {
        setCards([...cards, ...res]);
      }).catch((err) => {
        console.log(err)
      });
  }, [])

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__main-container">
          <div
            className="profile__picture-container"
            onClick={() => {
              setIsNewAvatarPopupOpen(true);
            }}
          >
            <img
              src={userAvatar}
              className="profile__image"
              alt="аватарка пользователя"
            />
            <img
              src={profileImageAfterPath}
              className="profile__avatar-edit"
              alt="Редактирование"
            />
          </div>
          <div className="profile__info">
            <div className="profile__text">
              <h1 className="profile__name">{userName}</h1>
              <p className="profile__about">{userDescription}</p>
            </div>
            <button
              type="button"
              className="profile__edit-button"
              aria-label="редактировать профиль"
              onClick={props.onEditProfile}
            />
          </div>
        </div>
        <button
          type="button"
          className="profile__button"
          onClick={() => {
            setIsCardAddPopupOpen(true);
          }}
        />
      </section>
      <section className="elements">
        {cards.map((card, i) => (
          <Card card={card} key={i} onClose={() => {
            setIsDeletePopupOpen(true);
          }} onCardClick={() => {
              console.log('click');
              setData({
                ...data,
                'name': `${card.name}`,
                'link': `${card.link}`,
              })
              setIsElementPopupOpen(true);
          }}/>
        )
        )}
        {/*___________overlays*_____________*/}

        {/*.element-popup.*/}
        {isElementPopupOpen && (
          <ImagePopup name={data.name} imageSrc={data.link} isOpen={true} onClose={() => {
            setIsElementPopupOpen(false);
          }}/>
        )}
      </section>

      {/*.new-avatar-popup.*/}
      {isNewAvatarPopupOpen && (
        <PopupWithForm
          name="new-avatar"
          title="Обновить аватар"
          formName="edit"
          formClass="popup__form_type_new-avatar"
          buttonLabel="Сохранить"
          isOpen={true}
          onClose={() => {
            setIsNewAvatarPopupOpen(false);
          }}
        >
          <input
            type="url"
            className="popup__input popup__input_type_avatarlink"
            name="avatarlink"
            id="avatarlink-input"
            required
            placeholder="Ссылка"
          />
          <span className="popup__form-placeholder avatarlink-input-error" />
        </PopupWithForm>
      )}

      {/*.card-delete-popup.*/}
      {isCardDeletePopupOpen && (
        <PopupWithForm
          name="card-delete"
          title="Вы уверены?"
          formName="delete"
          formClass="popup__form_type_delete-popup"
          buttonLabel="Да"
          isOpen={true}
          onClose={() => {
            setIsDeletePopupOpen(false);
          }}
        >
        </PopupWithForm>
      )}

      
    </main>
  );
}

export default Main;
