import React from 'react';
import deleteIconPath from "../images/element__delete-icon.svg";

function Card(card, onClose){
    console.log(card.card, onClose);
    return (
        <article className="element">
          <img src={card.link} className="element__image" alt={(card.name).toLowerCase()} />
          <img src={deleteIconPath} className="element__delete-icon" onClick={onClose} alt="иконка удаления"/>
          <div className="elements__ordinary-container">
            <h2 className="element__text">{card.name}</h2>
            <div className="element__like-container">
              <button type="button" className="element__like" />
              <span className="element__counter">{(card.likes).length}</span>
            </div>
          </div>
        </article>
    )
}

export default Card;
