import React from 'react';
import deleteIconPath from "../images/element__delete-icon.svg";

function Card(obj){
  function handleClick() {
    obj.onCardClick(card);
  }
    const newCard = obj.card;
    const functionOnClose = obj.onClose;
    return (
        <article className="element">
          <img src={newCard.link} className="element__image" onClick={handleClick} alt={(newCard.name).toLowerCase()} />
          <img src={deleteIconPath} className="element__delete-icon" onClick={functionOnClose} alt="иконка удаления"/>
          <div className="elements__ordinary-container">
            <h2 className="element__text">{newCard.name}</h2>
            <div className="element__like-container">
              <button type="button" className="element__like" />
              <span className="element__counter">{(newCard.likes).length}</span>
            </div>
          </div>
        </article>
    )
}

export default Card;
