import profileImagePath from "../images/profile__image.jpg";
import profileImageAfterPath from "../images/profile__image_after.svg";
import React from "react";
import PopupWithForm from "./PopupWithForm";

function Main() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);  {/* profile-edit-popup */}
  const [isNewAvatarPopupOpen, setIsNewAvatarPopupOpen] = React.useState(false);  {/* new-avatar-popup */}
  const [isOpen, setIsOpen] = useState(false);
 
  let popupWithForm = null;

  /*---------------------------*/
  let profileEditForm = (
    <PopupWithForm
      name="profile-edit"
      title="Редактировать профиль"
      formName="change"
      formClass="popup__form_type_edit"
      buttonLabel="Сохранить"
      isOpen={true}
      onClose={() => {
        setIsEditProfilePopupOpen(false);
      }}
    >
      <input
        type="text"
        className="popup__input popup__input_type_name"
        name="fullname"
        required
        id="fullname-input"
        minLength={2}
        maxLength={40}
        placeholder="Имя"
      />
      <span className="popup__form-placeholder fullname-input-error" />
      <input
        type="text"
        className="popup__input popup__input_type_about"
        name="about"
        id="about-input"
        required
        minLength={2}
        maxLength={200}
        placeholder="о себе"
      />
      <span className="popup__form-placeholder about-input-error" />
    </PopupWithForm>
  );

  return (
    <main className="content">
      {popupWithForm}
      <section className="profile">
        <div className="profile__main-container">
          <div
            className="profile__picture-container"
            onClick={() => {
              setIsOpen(true);
            }}
          >
            <img
              src={profileImagePath}
              className="profile__image"
              alt="аватарка пользователя"
            />
            <img
              src={profileImageAfterPath}
              className="profile__avatar-edit"
              alt="Редактирование"
            />
          </div>
          <div className="profile__info">
            <div className="profile__text">
              <h1 className="profile__name">Жак-Ив Кусто</h1>
              <p className="profile__about">Исследователь океана</p>
            </div>
            <button
              type="button"
              className="profile__edit-button"
              aria-label="редактировать профиль"
              onClick={() => {
                setIsEditProfilePopupOpen(true);
              }}
            />
          </div>
        </div>
        <button type="button" className="profile__button" />
      </section>
      <section className="elements">
        <template id="element" className="template" />
        {/*___________overlays*_____________*/}

        {/*--element-popup--*/}
        <div className="popup element-popup">
          <div className="element-popup__container">
            <button type="button" className="popup__close-icon" />
            <img
              src="<%=require('./images/elements__card__main-image(1).png')%>"
              className="element-popup__image"
              alt="Карачаево-Черкесия"
            />
            <p className="element-popup__text">Карачаево-Черкессия</p>
          </div>
        </div>
      </section>

      {isOpen && <PopupWithForm>
          
        </PopupWithForm>}
    </main>
  );
}

export default Main;
