import React from "react";

function PopupWithForm(props) {
  const [popupClass, setPopupClass] = React.useState(''); {/* не знаю как реализовать плавное открытие и закрытие попапа */}
  React.useEffect(() => {
    setPopupClass(`popup ${props.name}-popup popup_opened`); 
    return () => {
      setPopupClass(`popup ${props.name}-popup`);
    }
  })
  

  return (
    <article className={popupClass}>
      <div className={`popup__container ${props.name}-popup__container`}>
        <button
          type="button"
          className="popup__close-icon"
          onClick={props.onClose}
        />

        <h2 className="popup__title">{props.title}</h2>

        <form
          className={`popup__form ${props.formClass}`}
          onSubmit={(evt) => {
            evt.preventDefault();
            props.onClose();
          }}
          name={props.formName}
          noValidate
        >
          <fieldset className="popup__form-set">
            {props.children}
            <button type="submit" className="popup__button">
              {props.buttonLabel}
            </button>
          </fieldset>
        </form>
      </div>
    </article>
  );
}

export default PopupWithForm;
