import React, { useEffect } from "react";

function PopupWithForm(props) {
  let state = props.isOpen;
  let popupClass = state ? `popup ${props.name}-popup popup_opened` : `popup ${props.name}-popup`;
  console.log(popupClass);
  return (
    <article className={popupClass}>
      <div className="popup__container">
        <button
          type="button"
          className="popup__close-icon"
          onClick={() => {
              state = false;
              console.log(state);
              useEffect();
          }}
        />

        <h2 className="popup__title">{props.title}</h2>

        <form
          className={`popup__form ${props.formClass}`}
          name={props.formName}
          noValidate
        >
          <fieldset className="popup__form-set">
            {props.children}
            <button type="submit" className="popup__button" disabled>
              {props.buttonLabel}
            </button>
          </fieldset>
        </form>
      </div>
    </article>
  );
}

export default PopupWithForm;
